import PySimpleGUI as sg
from Controller import ControllerEnum

sg.theme("DarkPurple7")

on_image = b' '
off_image = b''
off_image_disabled = b''
on_image_disabled = b''

go2 = b''

# Class holding the button graphic info. At this time only the state is kept
class BtnInfo:
    def __init__(self, state=True):
        self.state = state        # Can have 3 states - True, False, None (disabled)


# Main function that creates the layout, window and has event loop, ((left, right), (top, bottom))
def show_ui():
    layout = [[sg.Text('Select features', font=("Lucida Sans", 40), pad=((20, 0), (20, 40)))],
              [sg.Text('Lane Detection:', pad=((50, 0), (0, 10))),
               sg.Button(image_data=off_image, k='LANE_DETECTION', border_width=0,
                         button_color=(sg.theme_background_color(), sg.theme_background_color()),
                         disabled_button_color=(sg.theme_background_color(), sg.theme_background_color()),
                         metadata=BtnInfo(False), pad=((40, 0), (0, 10)))
               ],
              [sg.HorizontalSeparator()],
              [sg.Text('Obstacle Detection:', pad=((50, 0), (0, 10))),
               sg.Button(image_data=off_image, k='OBSTACLE_DETECTION', border_width=0,
                         button_color=(sg.theme_background_color(), sg.theme_background_color()),
                         disabled_button_color=(sg.theme_background_color(), sg.theme_background_color()),
                         metadata=BtnInfo(False), pad=((5, 0), (0, 10)))
               ],
              [sg.HorizontalSeparator()],
              [sg.Text('Car Control:', pad=((50, 0), (0, 20))),
               sg.Combo(values=(ControllerEnum.MANUAL.value, ControllerEnum.CARLA.value, ControllerEnum.OUR.value),
                        default_value=ControllerEnum.MANUAL.value,
                        readonly=True, k='CONTROL', pad=((70, 0), (0, 20))),
               ],
              [sg.HorizontalSeparator()],
              [sg.Text('Num Cars:', pad=((50, 0), (0, 20))),
               sg.Slider(range=(0, 60), default_value=30, resolution=1,
                         orientation='h', key='NUM_CARS', pad=((83, 0), (0, 20))),
               ],
              [sg.HorizontalSeparator()],
              [sg.Text('Num Pedestrians:', pad=((50, 0), (0, 20))),
               sg.Slider(range=(0, 20), default_value=10, resolution=1,
                         orientation='h', key='NUM_PED', pad=((20, 0), (0, 20))),
               ],
              [sg.HorizontalSeparator()],
              [sg.Button('Start', image_data=go2, button_color=('black', sg.theme_background_color()),
                         border_width=0, font='Any 15', image_subsample=2, pad=((300, 0), (50, 20)))]
    ]

    window = sg.Window('Simulator Interface', layout, font='_ 14', finalize=True)

    while True:             # Event Loop
        event, values = window.read()
        if event in (sg.WIN_CLOSED, 'Start'):
            break
        # Where all the magic happens. 2 things happen when button is clicked
        # 1. The state toggles
        # 2. The button graphic changes
        elif event == 'LANE_DETECTION':
            window[event].metadata.state = not window[event].metadata.state
            window[event].update(image_data=on_image if window[event].metadata.state else off_image)
        elif event == 'OBSTACLE_DETECTION':
            window[event].metadata.state = not window[event].metadata.state
            window[event].update(image_data=on_image if window[event].metadata.state else off_image)

    od_selected = window['OBSTACLE_DETECTION'].metadata.state
    ld_selected = window['LANE_DETECTION'].metadata.state
    controller_selected = values['CONTROL']
    cars_to_spawn = values['NUM_CARS']
    pedestrians_to_spawn = values['NUM_PED']
    window.close()
    return ld_selected, od_selected, controller_selected, int(cars_to_spawn), int(pedestrians_to_spawn)


# Define the button graphic base 64 strings and then call the main function
if __name__ == '__main__':
    show_ui()
